mapper.select:
select coursename,groupname,st_no,username,sum(click_num) as click_num ,sum(online_num) as online_num,sum(viewres_num) as viewres_num,
sum(finshtask_num) as finshtask_num,sum(post_num) as post_num ,sum(not_task_num) as not_task_num,sum(not_post_num)  as not_post_num 
from dm_f_student_action_backup_history 
where   zzid like  #{condition.zzid}   and termid=#{condition.termId}
 group by coursename,groupname,st_no,username  
  order by ${page.sidx} ${page.sord}  limit #{page.start},#{page.rows}

select count(t.col1) from (select count(1) as col1  from dm_f_student_action_backup_history_history 
where  
	courseid=${courseID} AND fromsource=#{fromSource} 
	 and termid=#{termId}
  group by coursename,groupname,st_no,username) t

select coursename,groupname,st_no,username,sum(click_num) as click_num ,sum(online_num) as online_num,sum(viewres_num) as viewres_num,
sum(finshtask_num) as finshtask_num,sum(post_num) as post_num ,sum(not_task_num) as not_task_num,sum(not_post_num)  as not_post_num 
from dm_f_student_action_backup_history 
where 
 courseid=${condition.courseID} AND fromsource=#{condition.fromSource} 
  and termid=#{condition.termId}
 group by coursename,groupname,st_no,username  
  order by ${page.sidx} ${page.sord}  limit #{page.start},#{page.rows}

SELECT COUNT(tc0) FROM ( SELECT sa.termid tc0 FROM dm_f_student_action_backup_history sa LEFT JOIN dm_course c ON sa.courseid = c.id AND sa.fromsource = c.fromsource
sa.zzid LIKE CONCAT('${zzid}','%')
AND c.shortname = #{shortName}
AND termid=#{termId}
GROUP BY coursename,groupname,st_no,username ) t

SELECT coursename,groupname,st_no,username,sum(click_num) as click_num ,sum(online_num) as online_num,sum(viewres_num) as viewres_num, sum(finshtask_num) as finshtask_num,sum(post_num) as post_num ,sum(not_task_num) as not_task_num,sum(not_post_num) as not_post_num FROM dm_f_student_action_backup_history sa LEFT JOIN dm_course c ON sa.courseid = c.id AND sa.fromsource = c.fromsource
sa.zzid LIKE CONCAT('${zzid}','%')
AND c.shortname = #{shortName}
AND termid=#{termId}
GROUP BY coursename,groupname,st_no,username ORDER BY ${page.sidx} ${page.sord} limit #{page.start},#{page.rows}
